 1. \<lbrakk>distinct [];
     \<And>x. x \<in> set [] \<Longrightarrow> distinct (f x);
     \<And>x y.
        \<lbrakk>x \<in> set []; y \<in> set []; x \<noteq> y\<rbrakk>
        \<Longrightarrow> set (f x) \<inter> set (f y) = {}\<rbrakk>
    \<Longrightarrow> distinct (concat (map f []))
 2. \<And>a xs.
       \<lbrakk>\<lbrakk>distinct xs;
                 \<And>x. x \<in> set xs \<Longrightarrow> distinct (f x);
                 \<And>x y.
                    \<lbrakk>x \<in> set xs; y \<in> set xs;
                     x \<noteq> y\<rbrakk>
                    \<Longrightarrow> set (f x) \<inter> set (f y) =
{}\<rbrakk>
                \<Longrightarrow> distinct (concat (map f xs));
        distinct (a # xs);
        \<And>x. x \<in> set (a # xs) \<Longrightarrow> distinct (f x);
        \<And>x y.
           \<lbrakk>x \<in> set (a # xs); y \<in> set (a # xs);
            x \<noteq> y\<rbrakk>
           \<Longrightarrow> set (f x) \<inter> set (f y) = {}\<rbrakk>
       \<Longrightarrow> distinct (concat (map f (a # xs)))

