definition lcdh :: "'grp adversary \<Rightarrow> bool spmf"
where "lcdh \<A> = do { 
     x \<leftarrow> sample_uniform (order \<G>);
     y \<leftarrow> sample_uniform (order \<G>);
     zs \<leftarrow> \<A> (\<^bold>g [^] x) (\<^bold>g [^] y);
     return_spmf (\<^bold>g [^] (x * y) \<in> zs)
  }"
