lemma F_det    :
   "F(P \<box> Q) =    {(s,X). s = [] \<and> (s,X) \<in> F P \<inter> F Q}
                 \<union> {(s,X). s \<noteq> [] \<and> (s,X) \<in> F P \<union> F Q}
                 \<union> {(s,X). s = [] \<and> s \<in> D P \<union> D Q}
                 \<union> {(s,X). s = [] \<and> tick \<notin> X \<and> [tick] \<in> T P \<union> T Q}"

