have plus_le_conv2:
    "\<And>r f z. \<lbrakk> z \<in> err A; semilat (err A, r, f); OK x \<in> err A; OK y \<in> err A;
                 OK x \<squnion>\<^sub>f OK y \<sqsubseteq>\<^sub>r z\<rbrakk> \<Longrightarrow> OK x \<sqsubseteq>\<^sub>r z \<and> OK y \<sqsubseteq>\<^sub>r z"

proof (prove)
goal (2 subgoals):
 1. \<And>z.
       \<lbrakk>x \<in> A; y \<in> A; semilat (err A, Err.le r, lift2 f);
        x \<squnion>\<^bsub>f\<^esub> y = Err; z \<in> A;
        x \<sqsubseteq>\<^bsub>r\<^esub> z;
        y \<sqsubseteq>\<^bsub>r\<^esub> z\<rbrakk>
       \<Longrightarrow> False
 2. \<lbrakk>x \<in> A; y \<in> A; semilat (err A, Err.le r, lift2 f);
     \<not> (\<exists>z\<in>A.
                x \<sqsubseteq>\<^bsub>r\<^esub> z \<and>
                y \<sqsubseteq>\<^bsub>r\<^esub> z)\<rbrakk>
    \<Longrightarrow> x \<squnion>\<^bsub>f\<^esub> y = Err

