       \<lbrakk>P,E \<turnstile> e :: Boolean;
        P,E,h \<turnstile> e : Boolean; P,E \<turnstile> c :: T;
        P,E,h \<turnstile> c : T\<rbrakk>
       \<Longrightarrow> P,E,h \<turnstile> while (e) c : Void
 2. \<And>E e C.
       \<lbrakk>P,E \<turnstile> e :: Class C;
        P,E,h \<turnstile> e : Class C\<rbrakk>
       \<Longrightarrow> P,E,h \<turnstile> throw e : Void
 3. \<And>E e\<^sub>1 T V C e\<^sub>2.
       \<lbrakk>P,E \<turnstile> e\<^sub>1 :: T;
        P,E,h \<turnstile> e\<^sub>1 : T;
        P,E(V \<mapsto> Class C) \<turnstile> e\<^sub>2 :: T;
        P,E(V \<mapsto> Class C),h \<turnstile> e\<^sub>2 : T;
        is_class P C\<rbrakk>
       \<Longrightarrow> P,E,h \<turnstile> try e\<^sub>1
                         catch(C V) e\<^sub>2 : T
 4. \<And>E. P,E,h \<turnstile> [] [:] []
 5. \<And>E e T es Ts.
       \<lbrakk>P,E \<turnstile> e :: T; P,E,h \<turnstile> e : T;
        P,E \<turnstile> es [::] Ts; P,E,h \<turnstile> es [:] Ts\<rbrakk>
       \<Longrightarrow> P,E,h \<turnstile> e # es [:] T # Ts

